    SPECIALITE,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
    m.nom_coordinateur,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
    to_char(D_DEBUT_INSCRIPTION,'YYYY-MM-DD') as D_DEBUT_INSCRIPTION,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   
    to_char(D_FIN_INSCRIPTION,'YYYY-MM-DD') as D_FIN_INSCRIPTION ,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
